import { createTheme } from "@mui/material/styles";
import '../../styles/global.css'

const Theme = createTheme({
  palette: {
    background: {
      default: "#FBFBFB", // Set the default background color
      paper: "#FBFBFB", // Set the background color for surfaces like Paper
    },
    text: {
      primary: "#433D44", // Set the primary text color
    },
  },
  typography: {
    fontFamily: "Migra, sans-serif",
    h1: {
      fontFamily: "Anton-SC, sans-serif",
      fontSize: "12rem",
      fontWeight: 400,
    },
    body1: {
      fontSize: "1rem",
    },
  },
  components: {
    MuiTypography: {
      styleOverrides: {
        root: {
          color: "#433D44", // Custom text color
        },
      },
    },
  },
});

export default Theme;
