import { createTheme, ThemeProvider } from "@mui/material/styles";

const theme = createTheme({
  palette: {
    background: {
      default: "#FBFBFB", // Set the default background color
      paper: "#FBFBFB", // Set the background color for surfaces like Paper
    },
    text: {
      primary: "#433D44", // Set the primary text color
    },
  },
  typography: {
    fontFamily: "Migra, sans-serif",
    h1: {
      fontSize: "2.5rem",
      fontWeight: 700,
    },
    body1: {
      fontSize: "1rem",
    },
  },
  components: {
    MuiTypography: {
      styleOverrides: {
        root: {
          color: "#433D44", // Custom text color
        },
      },
    },
  },
});

export default theme;
